import PageHeader from "@/modules/core/components/PageHeader.vue";
import StorageEditForm from "@/modules/admin/storage/StorageEditForm.vue";
import ApplyDialog from "@/modules/admin/positions/ApplyDialog.vue";
import urls from "@/urls/models";
import roles from "@/urls/roles";
import { makeGetRequest, makeDeleteRequest } from "@/helper";
export default {
  name: "StorageList",
  components: {
    PageHeader,
    ApplyDialog,
    StorageEditForm
  },
  data() {
    return {
      title: 'Склад',
      count: 0,
      options: {},
      URL: urls.API + roles.SUPER_ADMIN + urls.REFERENCES.NAME + urls.REFERENCES.PATH.STORAGES,
      loadingPage: false,
      attrs: undefined,
      dialog_add_edit: false,
      dialog_delete: false,
      product_data: undefined,
      edit_product: undefined,
      items: undefined,
      headers: [{
        text: 'ID',
        value: 'id',
        align: 'start',
        sortable: false,
        width: '25px'
      }, {
        text: 'Номер',
        value: 'product_number',
        align: 'start',
        sortable: false
      }, {
        text: 'Наименование',
        value: 'product_name',
        align: 'start',
        sortable: true
      }, {
        text: 'Артикул',
        value: 'product_article',
        align: 'start',
        sortable: true
      }, {
        text: 'Кол-во',
        value: 'quantity',
        sortable: false,
        align: 'center'
      }, {
        text: 'Действия',
        value: 'actions',
        align: 'center',
        sortable: false,
        width: '100px'
      }]
    };
  },
  watch: {
    options: {
      handler() {
        this.loadData();
      },
      deep: true
    }
  },
  methods: {
    checkFlag(flag) {
      if (flag) return {
        icon: "check_circle",
        color: "green"
      };else return {
        icon: "not_interested",
        color: "grey"
      };
    },
    newProduct() {
      this.dialog_add_edit = true;
      this.edit_product = false;
      this.product_data = undefined;
    },
    editProduct(item) {
      this.dialog_add_edit = true;
      this.edit_product = true;
      this.product_data = item;
    },
    deleteProduct(item) {
      this.dialog_delete = true;
      this.product_data = item;
    },
    // eslint-disable-next-line no-unused-vars
    closeEditDialog(event) {
      this.loadData();
      this.dialog_add_edit = false;
      this.product_data = undefined;
    },
    closeDeleteDialog(event) {
      console.log('event', event);
      if (event) {
        let id_url = this.product_data.id + '/';
        makeDeleteRequest(this.URL + id_url);
      }
      setTimeout(() => {
        this.loadData();
        this.dialog_delete = false;
        this.product_data = undefined;
      }, 300);
    },
    updateData() {
      this.loadData();
    },
    loadData(url = undefined) {
      this.items = [];
      if (!url) {
        url = new URL(this.URL);
      }
      let params = {};
      if (this.options.page && this.options.itemsPerPage) {
        params.page = this.options.page;
        params.size = this.options.itemsPerPage;
      } else {
        params.page = "1";
        params.size = "10";
      }
      url.search = new URLSearchParams(params).toString();
      this.loadingPage = true;
      makeGetRequest(url).then(resp => resp.json()).then(data => {
        this.items = data.results;
        this.count = data.count;
        this.loadingPage = false;
      });

      // if (reload) {
      //   this.updateData()
      // }
    }
  },

  created() {
    // this.loadData()
  }
};
