import FormErrorsMixin from "@/mixins/FormErrorsMixin";
import { makeGetRequest, makePostRequest, makeUpdateRequest } from "@/helper";
import models from "@/urls/models";
import roles from "@/urls/roles";
import selectors from "@/urls/selectors";
import ScrollCenterDialog from "@/modules/templates/ScrollCenterDialog.vue";
export default {
  name: "UserEditForm",
  components: {
    ScrollCenterDialog
  },
  mixins: [FormErrorsMixin],
  props: {
    opened: Boolean,
    edit: Boolean,
    userData: Object
  },
  data() {
    return {
      URL_POS: selectors.API + selectors.TEMPS.POSITIONS,
      URL_USR: models.API + roles.SUPER_ADMIN + models.CONTACTS.NAME + models.CONTACTS.PATH.USER,
      loading: false,
      is_admin: false,
      is_staff: false,
      is_booker: false,
      user: {
        date: new Date(Date.now() - new Date().getTimezoneOffset() * 60000).toISOString().substr(0, 10)
      },
      sex: ['М', 'Ж'],
      positions: undefined,
      pos_field: undefined,
      save_loading: false
    };
  },
  methods: {
    close() {
      this.$emit('close', false);
    },
    save() {
      let data = {
        name: this.user,
        department: this.pos_field,
        is_head: this.is_head
      };
      if (this.edit) {
        let id = this.posData.id;
        data.department = data.department.value;
        this.changePos(data, id);
      } else {
        this.createPos(data);
      }
    },
    getDepartments() {
      makeGetRequest(this.URL_DEP).then(resp => resp.json()).then(data => {
        this.departments = data;
      });
    },
    createPos(data) {
      makePostRequest(this.URL_POS, data);
      setTimeout(() => {
        this.save_loading = false;
        this.$emit('close', this.user);
      }, 300);
    },
    changePos(data, id) {
      let url_id = id + '/';
      makeUpdateRequest(this.URL_POS + url_id, data);
      setTimeout(() => {
        this.save_loading = false;
        this.$emit('close', this.user);
      }, 300);
    },
    load_edit_item() {
      if (this.posData) {
        this.user = this.posData.name;
        this.pos_field = this.posData.department;
        // this.dep_field = {value: this.posData.department.value, text: this.posData.department.text}
        this.is_head = this.posData.is_head;
      }
    }
  },
  created() {
    this.getDepartments();
    this.load_edit_item();
  },
  computed: {
    form_title() {
      return this.edit === false ? 'Добавить должность' : 'Редактировать должность';
    }
  }
};
