<template>
  <scroll-center-dialog
      v-model="
      /* eslint-disable-next-line vue/no-mutating-props */
      opened"
      :loading="save_loading"
      width="600"
      @close="close"
      @apply="save"
      :title="form_title"
      :scrolled="false"
  >
    <v-text-field
        v-model="org.number"
        label="Номер"
        :error="hasError('full_name')"
        :errorMessages="getError('full_name')"
        class="mb-n2"
    ></v-text-field>
    <v-text-field
        v-model="org.Document"
        label="Название документ"
        :error="hasError('short_name')"
        :errorMessages="getError('short_name')"
        class="mb-n2"
    ></v-text-field>
    <v-autocomplete
        class="mt-4"
        v-model="org.contact_person"
        :items="users"
        item-value="value"
        item-text="text"
        label="Статус"
        :error="hasError('value')"
        :errorMessages="getError('value')"
    ></v-autocomplete>
    <v-autocomplete
        class="mt-4"
        v-model="org.contact_person"
        :items="users"
        item-value="value"
        item-text="text"
        label="Контактное лицо"
        :error="hasError('value')"
        :errorMessages="getError('value')"
    ></v-autocomplete>
  </scroll-center-dialog>
</template>

<script>
import FormErrorsMixin from "@/mixins/FormErrorsMixin"
import {makeGetRequest, makePostRequest, makeUpdateRequest} from "@/helper";
import models from "@/urls/models";
import roles from "@/urls/roles";
import selectors from "@/urls/selectors";
import ScrollCenterDialog from "@/modules/templates/ScrollCenterDialog.vue";

export default {
  name: "OrgEditForm",
  components: {ScrollCenterDialog},
  mixins: [FormErrorsMixin],
  props: {
    opened: Boolean,
    edit: Boolean,
    orgData: Object,
  },
  data() {
    return {
      URL_STAT: selectors.API + selectors.TEMPS.STATUSES,
      URL_ORG: selectors.API + selectors.CONTACTS.ORGANIZATIONS,
      URL_CONT: models.API + roles.SUPER_ADMIN + models.REFERENCES.NAME + models.REFERENCES.PATH.CONTRACTS,
      loading: false,
      org: {
        bank_name: undefined,
        bank_number: undefined,
        bic: undefined,
        contact_person: undefined,
        corres_number: undefined,
        full_name: undefined,
        id: undefined,
        inn: undefined,
        kpp: undefined,
        legal_address:  undefined,
        mail_address: undefined,
        short_name: undefined,
      },
      users: undefined,
      save_loading:false,
    }
  },
  methods: {
    close() {
      this.$emit('close', false)
    },
    save() {
      let data
      data = this.aggregateData(this.org)

      if (this.edit) {
        let id = this.orgData.id
        this.changeOrg(data, id)
      } else {
        this.createOrg(data)
      }
    },
    getUsers() {
      makeGetRequest(this.URL_USR)
          .then(resp => resp.json())
          .then(data => {
            this.users = data
          })
    },
    aggregateData(array) {
      let data = {}
      console.log(array)
      for (let key in array) {
        if (key === 'contact_person') {
          data[key] = array[key]
          continue
        }
        data[key] = array[key]
      }
      return data
    },
    createOrg(data) {
      makePostRequest(this.URL_ORG, data)
      setTimeout(() => {
        this.save_loading = false
        this.$emit('close', this.org)
      }, 800)
    },
    changeOrg(data, id) {
      let url_id = id + '/'
      makeUpdateRequest(this.URL_ORG + url_id, data)
      setTimeout(() => {
        this.save_loading = false
        this.$emit('close', this.org)
      }, 800)
    },
    load_edit_item() {
      if (this.orgData) {
        for (let key in this.orgData) {
          if (key in this.org) {
            this.org[key] = this.orgData[key]
          }
        }
      }
    },
  },

  created() {
    this.getUsers()
    this.load_edit_item()
  },
  computed: {
    form_title() {
      return this.edit === false ? 'Добавить организацию' : 'Редактировать организацию'
    },
  }
}
</script>

