import PageHeader from "@/modules/core/components/PageHeader.vue";
import OperationEditForm from "@/modules/admin/storage/OperationEditForm.vue";
import ApplyDialog from "@/modules/admin/storage/ApplyDialog.vue";
import urls from "@/urls/models";
import roles from "@/urls/roles";
import { makeGetRequest, makeDeleteRequest } from "@/helper";
export default {
  name: "OperationList",
  components: {
    PageHeader,
    ApplyDialog,
    OperationEditForm
  },
  data() {
    return {
      title: 'Операции по складу',
      count: 0,
      width: '900px',
      options: {},
      URL: urls.API + roles.SUPER_ADMIN + urls.REFERENCES.NAME + urls.REFERENCES.PATH.OPERATIONS,
      loadingPage: false,
      attrs: undefined,
      dialog_add_edit: false,
      dialog_delete: false,
      oper_data: undefined,
      edit_status: undefined,
      items: undefined,
      headers: [{
        text: 'ID',
        value: 'id',
        align: 'start',
        sortable: false,
        width: '25px'
      }, {
        text: 'Номер операции',
        value: 'number_operation',
        align: 'start',
        sortable: false
      }, {
        text: 'Заказ',
        value: 'order.text',
        align: 'start',
        sortable: true
      }, {
        text: 'Дата операции',
        value: 'date_time',
        align: 'start',
        sortable: true
      }, {
        text: 'Товар',
        value: 'product.text',
        sortable: false,
        align: 'center'
      }, {
        text: 'Тип операции',
        value: 'operation_type.text',
        sortable: false,
        align: 'center'
      }, {
        text: 'Действия',
        value: 'actions',
        align: 'center',
        sortable: false,
        width: '100px'
      }]
    };
  },
  watch: {
    options: {
      handler() {
        this.loadData();
      },
      deep: true
    }
  },
  methods: {
    newOper() {
      this.dialog_add_edit = true;
      this.edit_status = false;
      this.oper_data = undefined;
    },
    editOper(item) {
      this.dialog_add_edit = true;
      this.edit_status = true;
      this.oper_data = item;
    },
    deleteOper(item) {
      this.dialog_delete = true;
      this.oper_data = item;
    },
    // eslint-disable-next-line no-unused-vars
    closeEditDialog(event) {
      this.loadData();
      this.dialog_add_edit = false;
      this.oper_data = undefined;
    },
    closeDeleteDialog(event) {
      console.log('event', event);
      if (event) {
        let id_url = this.oper_data.id + '/';
        makeDeleteRequest(this.URL + id_url);
      }
      setTimeout(() => {
        this.loadData();
        this.dialog_delete = false;
        this.oper_data = undefined;
      }, 700);
    },
    updateData() {
      this.loadData();
    },
    loadData(url = undefined) {
      this.items = [];
      if (!url) {
        url = new URL(this.URL);
      }
      let params = {};
      if (this.options.page && this.options.itemsPerPage) {
        params.page = this.options.page;
        params.size = this.options.itemsPerPage;
      } else {
        params.page = "1";
        params.size = "10";
      }
      url.search = new URLSearchParams(params).toString();
      this.loadingPage = true;
      makeGetRequest(url).then(resp => resp.json()).then(data => {
        this.items = data.results;
        this.count = data.count;
        this.loadingPage = false;
      });

      // if (reload) {
      //   this.updateData()
      // }
    }
  },

  created() {
    // this.loadData()
  }
};
