import PageHeader from "@/modules/core/components/PageHeader.vue";
import OrderEditForm from "@/modules/admin/order/OrderEditForm.vue";
import ApplyDialog from "@/modules/admin/order/ApplyDialog.vue";
import urls from "@/urls/models";
import roles from "@/urls/roles";
import { makeGetRequest, makeDeleteRequest } from "@/helper";
export default {
  name: "OrderList",
  components: {
    PageHeader,
    ApplyDialog,
    OrderEditForm
  },
  data() {
    return {
      title: 'Организации',
      count: 0,
      width: '900px',
      options: {},
      URL: urls.API + roles.SUPER_ADMIN + urls.ORDERS.NAME + urls.ORDERS.PATH.ORDERS,
      loadingPage: false,
      attrs: undefined,
      dialog_add_edit: false,
      dialog_delete: false,
      order_data: undefined,
      edit_status: undefined,
      items: undefined,
      headers: [{
        text: 'ID',
        value: 'id',
        align: 'start',
        sortable: false,
        width: '25px'
      }, {
        text: 'Номер',
        value: 'order_number',
        align: 'start',
        sortable: false
      }, {
        text: 'Поставщик',
        value: 'provider',
        align: 'start',
        sortable: false
      }, {
        text: 'Контактное лицо',
        value: 'contact_person.text',
        align: 'start',
        sortable: false
      }, {
        text: 'Дата регистрации',
        value: 'order_reg_date',
        align: 'start',
        sortable: true
      }, {
        text: 'Номер контракта',
        value: 'contract_number',
        align: 'start',
        sortable: false
      }, {
        text: 'Менеджер',
        value: 'purchase_manager',
        align: 'start',
        sortable: false
      }, {
        text: 'Действия',
        value: 'actions',
        align: 'center',
        sortable: false,
        width: '100px'
      }]
    };
  },
  watch: {
    options: {
      handler() {
        this.loadData();
      },
      deep: true
    }
  },
  methods: {
    checkFlag(flag) {
      if (flag) return {
        icon: "check_circle",
        color: "green"
      };else return {
        icon: "not_interested",
        color: "grey"
      };
    },
    newOrder() {
      this.dialog_add_edit = true;
      this.edit_status = false;
      this.order_data = undefined;
    },
    editOrder(item) {
      this.dialog_add_edit = true;
      this.edit_status = true;
      this.order_data = item;
    },
    deleteOrder(item) {
      this.dialog_delete = true;
      this.order_data = item;
    },
    // eslint-disable-next-line no-unused-vars
    closeEditDialog(event) {
      this.loadData();
      this.dialog_add_edit = false;
      this.order_data = undefined;
    },
    closeDeleteDialog(event) {
      console.log('event', event);
      if (event) {
        let id_url = this.order_data.id + '/';
        makeDeleteRequest(this.URL + id_url);
      }
      setTimeout(() => {
        this.loadData();
        this.dialog_delete = false;
        this.order_data = undefined;
      }, 700);
    },
    updateData() {
      this.loadData();
    },
    loadData(url = undefined) {
      this.items = [];
      if (!url) {
        url = new URL(this.URL);
      }
      let params = {};
      if (this.options.page && this.options.itemsPerPage) {
        params.page = this.options.page;
        params.size = this.options.itemsPerPage;
      } else {
        params.page = "1";
        params.size = "10";
      }
      url.search = new URLSearchParams(params).toString();
      this.loadingPage = true;
      makeGetRequest(url).then(resp => resp.json()).then(data => {
        this.items = data.results;
        this.count = data.count;
        this.loadingPage = false;
      });

      // if (reload) {
      //   this.updateData()
      // }
    }
  },

  created() {
    // this.loadData()
  }
};
